import{_ as t,c as o,o as a,a3 as n}from"./chunks/framework.DcN6MIyB.js";const f=JSON.parse('{"title":"About NIOS","description":"","frontmatter":{},"headers":[],"relativePath":"wiki/About.md","filePath":"wiki/About.md","lastUpdated":1746124387000}'),i={name:"wiki/About.md"};function r(s,e,l,c,u,d){return a(),o("div",null,e[0]||(e[0]=[n('<h2 id="about-nios-us" tabindex="-1">About NIOS &amp; Us <a class="header-anchor" href="#about-nios-us" aria-label="Permalink to &quot;About NIOS &amp; Us&quot;">​</a></h2><h2 id="about-us" tabindex="-1">About Us <a class="header-anchor" href="#about-us" aria-label="Permalink to &quot;About Us&quot;">​</a></h2><p>We are a community of NIOS students from various platforms, dedicated to creating helpful wikis, guides, and how-tos for free!<br> T_T Thank you for all the support!</p><h1 id="about-nios" tabindex="-1">About NIOS <a class="header-anchor" href="#about-nios" aria-label="Permalink to &quot;About NIOS&quot;">​</a></h1><p><strong>The National Institute of Open Schooling (NIOS),</strong> formerly known as the National Open School (NOS), was established in November 1989 as an autonomous organisation in pursuance of the National Policy on Education, 1986, by the Ministry of Education (MoE), Government of India. NIOS offers a wide range of vocational, life enrichment, and community-oriented courses, in addition to general and academic courses at the secondary and senior secondary levels. It also provides elementary-level courses through its Open Basic Education Programmes (OBE).</p><p>The Government of India, through a gazette notification, granted NIOS the authority to examine and certify learners registered with it up to pre-degree level courses, whether academic, technical, or vocational. The Association of Indian Universities, via letter No. EV/11(354)/91 dated 25 July 1991, recognized the equivalence of the Senior Secondary Certificate Examination conducted by NIOS.</p><p>For more information, please visit the following websites:</p><ul><li><a href="https://www.nios.ac.in/" target="_blank" rel="noreferrer">NIOS (National Institute of Open Schooling)</a></li><li><a href="https://dsel.education.gov.in/nios" target="_blank" rel="noreferrer">Department of School Education and Literacy</a></li></ul><p>For assistance, you can contact the NIOS Toll-free Helpline Number: <strong>1800-180-9393</strong>, or write an email to <strong>lsc[at]nios.ac.in</strong>.</p><p>The helpline is available from <strong>9:00 AM to 5:30 PM</strong> on working days. <s>Please note that there may be a wait time of 1-2 hours.</s></p><p><a href="https://www.nios.ac.in/contact-us.aspx" target="_blank" rel="noreferrer">Click here for contact details of the headquarters, regional offices, and more.</a></p>',11)]))}const p=t(i,[["render",r]]);export{f as __pageData,p as default};
