import{_ as t,c as o,o as a,a3 as r}from"./chunks/framework.DcN6MIyB.js";const u=JSON.parse('{"title":"Terms of TMA and the date of feedback of TMA","description":"","frontmatter":{},"headers":[],"relativePath":"wiki/how to make TMA.md","filePath":"wiki/how to make TMA.md","lastUpdated":1746124387000}'),n={name:"wiki/how to make TMA.md"};function s(i,e,l,d,c,h){return a(),o("div",null,e[0]||(e[0]=[r('<h2 id="how-to-make-tma" tabindex="-1">How to Make TMA? <a class="header-anchor" href="#how-to-make-tma" aria-label="Permalink to &quot;How to Make TMA?&quot;">​</a></h2><ol><li><strong>First download the TMA questions paper from dashboard</strong><br><a href="https://sdmis.nios.ac.in" target="_blank" rel="noreferrer">NIOS Student Portal</a></li><li>Go TMA window <strong>Click on Download TMA</strong> Download the questions.</li><li><strong>Prep work</strong><br> Get a blue pen, ruler, pencil, and a notebook or A4 sheets, any kind of paper is fine.</li><li>Download a document scanner of your choice like <a href="https://apt.izzysoft.de/fdroid/index/apk/com.akylas.documentscanner" target="_blank" rel="noreferrer">OSS Document Scanner</a> or <a href="https://play.google.com/store/apps/details?id=com.cambyte.okenscan" target="_blank" rel="noreferrer">Okenscan</a></li><li><strong>Download the solved TMAs or start finding the answers from the textbook.</strong></li></ol><div class="note custom-block github-alert"><p class="custom-block-title">NOTE</p><p><a href="https://drive.google.com/drive/folders/1eFHUKAtpzh-rBjDm4k4zfn3_AZmu0Byh" target="_blank" rel="noreferrer">Download for FREE</a> Note: These are computzied not handwritten. So, you have copy on paper.</p></div><ol start="6"><li><strong>Front page</strong><br> Write the following information on the first page.</li></ol><p>Name:</p><p>Enrollment No: <em>Check your ID card or dashboard</em></p><p>Subject name and code:</p><p>Medium of study:</p><p>Study centre/AI name and AI code:<br><strong>Both name and code check are written all over the dashboard. Here&#39;s one of many ways to check it. dashboard-&gt;Print-&gt;ID Card. It&#39;s on the blue section or dashboard-&gt;My Details.</strong> 7. <strong>Answer writing</strong></p><p>Every question has two options <em>A</em> and <em>B</em>. Make sure that you write the question number as follows: 1 A ans.</p><div class="tip custom-block github-alert"><p class="custom-block-title">TIP</p><p>At the top right corner of the page write pg-1 and so on**</p></div><ol start="8"><li><strong>Scanning of TMA</strong><br> Make sure you are in a well-lighted area. Open the Oken scanner app or any other scanner app. Click pictures of the TMA ensuring that the full page is in the frame.</li><li><strong>Uploading of TMA(s)</strong> Go to <a href="https://sdmis.nios.ac.in" target="_blank" rel="noreferrer">https://sdmis.nios.ac.in</a> Under TMA window click on TMA upload. A list of all your TMA will appear. Click the subject you wish to upload, <strong>choose your medium of study</strong>, click on upload, and find the file of that subject and wait for few seconds it&#39;ll prompt you to check it again. Click on proceed.</li></ol><div class="warning custom-block github-alert"><p class="custom-block-title">WARNING</p><p>The limit is <strong>5MB</strong>. Check if your TMA was uploaded from TMA Status via your dashboard under TMA Status. If you think there is any mistake in the TMA you can &quot;Re-Upload&quot; it for <strong>free!</strong> until 7 days from the day of uploading it.</p></div><h1 id="terms-of-tma-and-the-date-of-feedback-of-tma" tabindex="-1">Terms of TMA and the date of feedback of TMA <a class="header-anchor" href="#terms-of-tma-and-the-date-of-feedback-of-tma" aria-label="Permalink to &quot;Terms of TMA and the date of feedback of TMA&quot;">​</a></h1><ul><li>TMA Submission pending: TMA isn&#39;t upload yet*</li><li>Upload online: TMA is upload on the NIOS server*</li><li>TMA Allocated: When the study centre gets your TMA(s) and accepts it</li><li>TMA Evaluation in progress: The teacher is checking</li><li>TMA Evaluated: Your TMA is corrected/check*</li><li>&quot;-&quot;: You passed in the subject</li></ul><div class="note custom-block github-alert"><p class="custom-block-title">NOTE</p><p>Whatever is marked with “*” are important. The dates can differ from student to student but it takes ton of time for April 2024 the feedback/marks were given to everyone by the 31 April 2024.</p></div>',16)]))}const f=t(n,[["render",s]]);export{u as __pageData,f as default};
