import{_ as a,c as t,o as i,a3 as o}from"./chunks/framework.DcN6MIyB.js";const f=JSON.parse(`{"title":"Scribe Info","description":"","frontmatter":{"order":0,"title":"Scribe Info"},"headers":[],"relativePath":"wiki/howto's-scribe.md","filePath":"wiki/howto's-scribe.md","lastUpdated":1746124387000}`),n={name:"wiki/howto's-scribe.md"};function r(l,e,s,c,h,d){return i(),t("div",null,e[0]||(e[0]=[o(`<h2 id="how-to-apply-for-a-scribe" tabindex="-1">How To apply for a scribe? <a class="header-anchor" href="#how-to-apply-for-a-scribe" aria-label="Permalink to &quot;How To apply for a scribe?&quot;">​</a></h2><div class="note custom-block github-alert"><p class="custom-block-title">NOTE</p><p>I&#39;m writing from experience as I took the April 2024 exams with a scribe. Firstly, NIOS or the exam center will not provide a scribe/writer. You have to find one by yourself.</p></div><h1 id="documents-required" tabindex="-1">Documents Required <a class="header-anchor" href="#documents-required" aria-label="Permalink to &quot;Documents Required&quot;">​</a></h1><ol><li><a href="https://nios.ac.in/media/documents/PWD/Application%20by%20Parent%20Form1.pdf" target="_blank" rel="noreferrer">Application by the Parent*</a></li><li><a href="https://nios.ac.in/media/documents/PWD/Medical%20certificate.pdf" target="_blank" rel="noreferrer">Medical Certificate*</a></li><li><a href="https://www.digilocker.gov.in/" target="_blank" rel="noreferrer">Copy of Disability Certificate*</a></li><li>Bonafide Certificate*</li><li>Student ID card of the school and Aadhaar card of the writer/scribe*</li><li><a href="https://sdmis.nios.ac.in/registration/id-card" target="_blank" rel="noreferrer">NIOS ID card</a> of the learner or hallticket (if available)*</li></ol><div class="warning custom-block github-alert"><p class="custom-block-title">WARNING</p><p>Xeroxs are fine for SlNo 3, 5, and 6</p></div><h1 id="how-can-i-find-and-fill-these-documents" tabindex="-1">How can I find and fill these documents? <a class="header-anchor" href="#how-can-i-find-and-fill-these-documents" aria-label="Permalink to &quot;How can I find and fill these documents?&quot;">​</a></h1><p>First, visit the NIOS website and download the Application by the Parent and Medical Certificate from the <a href="https://nios.ac.in/departmentsunits/evaluation/general-and-specific-relaxations-(disability-wise)-during-nios-examination.aspx" target="_blank" rel="noreferrer">Provisions for Learners with Disability</a>. Take a printout of these documents.</p><h1 id="how-to-fill-out-the-application-by-the-parent" tabindex="-1">How to fill out the Application by the Parent: <a class="header-anchor" href="#how-to-fill-out-the-application-by-the-parent" aria-label="Permalink to &quot;How to fill out the Application by the Parent:&quot;">​</a></h1><ol><li>Name of the person filling out the form</li><li>Relationship to the learner</li><li>Permanent address</li><li>Phone number</li><li>Email address</li><li>Today&#39;s date</li><li>The name of your RC (see your ID card right side and write the name of the city)</li><li>Tick one option (use a black pen)</li><li>Start and end date of the exam (if the datesheet isn&#39;t released yet, write the first and last date of the months. For example, &quot;April exam &#39;1st of April year&#39; to &#39;1st of May year&#39;&quot;)</li><li>Tick the correct option</li><li>Name of the learner</li><li>Enrollment number of the learner</li><li>Tick the course enrolled in</li><li>Write the nature of the disability (e.g., if you have low vision, write &quot;Blindness&quot;)</li><li>Get your disability certificate and write the hospital&#39;s name there</li><li>Sl. No should be at the top right corner write that in “Sl. No”</li><li>Write the date given on the disability certificate</li><li>Fill the table with the subjects you paid exam fees for</li><li>Write &quot;Yes&quot; under &quot;Self-arranged&quot; for all subjects. Write &quot;Scribe&quot; in the right-side column.</li></ol><h1 id="performa-for-information-regarding-amanuensis" tabindex="-1">Performa for information regarding Amanuensis <a class="header-anchor" href="#performa-for-information-regarding-amanuensis" aria-label="Permalink to &quot;Performa for information regarding Amanuensis&quot;">​</a></h1><ol><li>Photo</li><li>Name of the Scribe</li><li>Name of the Father/Mother/Guardian</li><li>Details of Educational Qualification:</li><li>Tick the correct option</li><li>Write Information of the candidate/learner</li><li>Write Subject name and date of exam</li><li>Date, Signature, and stamp</li></ol><h1 id="nedical-certificate" tabindex="-1">Nedical Certificate <a class="header-anchor" href="#nedical-certificate" aria-label="Permalink to &quot;Nedical Certificate&quot;">​</a></h1><p><strong>Is a medical certificate required?</strong> Yes, <em>without</em> the medical certificate, the form will not be accepted.</p><p><strong>How to fill the Medical Certificate:</strong></p><ol><li>Who can fill it? The hospital you go to for checkups or any government hospital.</li><li>Make sure that you have the signature and seal of the doctor and hospital.</li><li>Write this in the last line: &quot;e) Blindness/Low-vision&quot; and &quot;Scribe and reader is required.&quot;</li></ol><h1 id="how-to-fill-out-bonafide-certificate" tabindex="-1">How to fill out Bonafide Certificate: <a class="header-anchor" href="#how-to-fill-out-bonafide-certificate" aria-label="Permalink to &quot;How to fill out Bonafide Certificate:&quot;">​</a></h1><div class="tip custom-block github-alert"><p class="custom-block-title">TIP</p><p>Just fill in the format given below:</p></div><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Bonafide Certificate</span></span>
<span class="line"><span></span></span>
<span class="line"><span>This is to certify that [Name of the scribe] is a bonafide student of the above-cited school, i.e., [Name of school with address]. As per the admission register, his Adm. No is [Adm. No]. He joined the school in the academic year [academic year] for Std [Grade when he/she was admitted to the school]. His date of birth given is [DOB].</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Date:</span></span>
<span class="line"><span></span></span>
<span class="line"><span>He is currently studying in Std [Current Grade] for the academic year [Academic year].</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Principal/Head of the Institution  </span></span>
<span class="line"><span>Principal/Correspondent</span></span>
<span class="line"><span>  </span></span>
<span class="line"><span>[Signature]  </span></span>
<span class="line"><span>[Seal of the school]</span></span></code></pre></div><h1 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h1><ol><li>Scan all documents using <a href="https://play.google.com/store/apps/details?id=com.cambyte.okenscan" target="_blank" rel="noreferrer">Oken Scanner</a>.</li><li>Mail them to your RC <a href="https://sdmis.nios.ac.in/home/regional-center" target="_blank" rel="noreferrer">contact info Regional Center</a>.</li></ol><div class="caution custom-block github-alert"><p class="custom-block-title">CAUTION</p><p>You <strong>must send</strong> the Documents physically.</p></div><p><strong>Important points to remember</strong></p><ul><li>Do not wait for the datesheet. Send the documents to the RC after you pay the exam fees.</li><li>The RC will mail you a letter. Take a printout of that letter and use it.</li></ul><h2 id="here-s-a-copy-of-mine" tabindex="-1">Here&#39;s a copy of mine <a class="header-anchor" href="#here-s-a-copy-of-mine" aria-label="Permalink to &quot;Here&#39;s a copy of mine&quot;">​</a></h2><img src="https://cdn.jsdelivr.net/gh/nios-students/docs@master/wiki/assets/image0.jpg" alt="Letter" width="400" height="567"><p><strong>research and Written by pingforhelp</strong></p><div class="tip custom-block github-alert"><p class="custom-block-title">TIP</p><p>For any questions please e-mail @ <a href="mailto:unofficianios@gmail.com" target="_blank" rel="noreferrer">unofficianios[AT]gmail(dot)com</a></p></div>`,27)]))}const u=a(n,[["render",r]]);export{f as __pageData,u as default};
